pub const Request = struct {
    message: Message,
    dict: *Dictionary,

    // used for list type of response
    arena: *std.heap.ArenaAllocator,  // Change to pointer

    pub fn init(allocator: std.mem.Allocator, m: Message, d: *Dictionary) Request {
        var arena = allocator.create(std.heap.ArenaAllocator) catch unreachable;
        arena.* = std.heap.ArenaAllocator.init(allocator);

        return Request{
            .message = m,
            .dict = d,
            .arena = arena,
        };
    }

    pub fn deinit(self: *Request) void {
        self.arena.deinit();
        self.message.arena.allocator().destroy(self.arena);
    }
};

